package ua.kpi;/** * Created by Evgeniy on 11/2/2016. * Demonstrates program logic of working with arrays */public class Model {    /**     * Constants of min and max value of elements in array     */    public static final int MIN = -100;    public static final int MAX = 100;    /**     * Calculate the sum of all elements in array     * @param array of integers     * @return sum of elements     */    public int findSum(int array[]){        int sum = 0;        for (int element : array){            sum += element;        }        return sum;    }    /**     * Find index and value of max element in array     * @param array     * @return index     */    public int findMax(int array[]){        int max = array[0];        int index = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] > max){                max = array[i];                index = i;            }        }        return max;    }    /**     * Find index and value of min element in array     * @param array     * @return index     */    public int findMin(int array[]){        int min = array[0];        int index = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] < min){                min = array[i];                index = i;            }        }        return index;    }    /**     * Find average value of array's elements     * @param array     * @return average value     */    public  double averageValue(int array[]){        return (double)findSum(array) / array.length;    }    /**     * Find quantity of elements equal to given     * @param value     * @param array     * @return quantity     */    public int quantityElementsEqualToGiven(double value, int array[]){        int quantity = 0;        for (int element : array){            if (element == value){                quantity++;            }        }        return quantity;    }    /**     * Find quantity of elements equal to zero     * @param array     * @return quantity     */    public int quantityElemsEqualToZero(int array[]){        int quantity = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] == 0){                quantity++;            }        }        return quantity;    }    /**     * Find quantity of elements greater than zero     * @param array     * @return quantity     */    public int quantityOfPositiveElements(int array[]){        int quantity = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] > 0){                quantity++;            }        }        return quantity;    }    /**     * Multiply elements of array by given value     * @param array     * @param value     * @return resultArray     */    public int[] multiplyArrayByValue(int array[], int value){        int resultArray[] = new int[array.length];        for (int i = 0; i < array.length; i++) {            resultArray[i] = array[i] * value;        }        return resultArray;    }}