package ua.kpi;/** * Created by Evgeniy on 11/2/2016. */public class Model {    public static final int MIN = -100;    public static final int MAX = 100;    public int findSum(int array[]){        int sum = 0;        for (int element : array){            sum += element;        }        return sum;    }    public int findMax(int array[]){        int max = array[0];        int index = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] > max){                max = array[i];                index = i;            }        }        return max;    }    public int findMin(int array[]){        int min = array[0];        int index = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] < min){                min = array[i];                index = i;            }        }        return index;    }}